import { Component, OnInit } from '@angular/core';
import { ApiService } from '../../core/api/api.service';

@Component({
  selector: 'app-logs',
  template: `
    <div class="logs">
      <h2>Logs de Sincronização</h2>
      <div *ngIf="loading" class="loading">Carregando...</div>
      <div *ngIf="logs && logs.length > 0" class="logs-list">
        <div *ngFor="let log of logs" class="log-item" [class.error]="log.status === 'error'">
          <div class="log-header">
            <span class="log-type">{{ log.tipo }}</span>
            <span class="log-status" [class.success]="log.status === 'success'">
              {{ log.status }}
            </span>
          </div>
          <div class="log-body">
            <p>{{ log.mensagem }}</p>
            <small>{{ log.datacadastro | date: 'short' }}</small>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [
    `
      .logs {
        padding: 2rem;
      }
      .logs-list {
        margin-top: 1rem;
      }
      .log-item {
        background: white;
        padding: 1rem;
        margin-bottom: 0.5rem;
        border-radius: 4px;
        border-left: 4px solid #1976d2;
      }
      .log-item.error {
        border-left-color: #f44336;
      }
      .log-header {
        display: flex;
        justify-content: space-between;
        margin-bottom: 0.5rem;
      }
      .log-type {
        font-weight: bold;
      }
      .log-status {
        padding: 0.25rem 0.5rem;
        border-radius: 4px;
        background-color: #f44336;
        color: white;
        font-size: 0.875rem;
      }
      .log-status.success {
        background-color: #4caf50;
      }
      .log-body small {
        color: #666;
      }
    `,
  ],
})
export class LogsComponent implements OnInit {
  logs: any[] = [];
  loading = false;

  constructor(private api: ApiService) {}

  ngOnInit(): void {
    this.loadLogs();
  }

  loadLogs(): void {
    this.loading = true;
    this.api.get('/admin/logs').subscribe({
      next: (response) => {
        if (response.success && response.data) {
          this.logs = response.data.data || [];
        }
        this.loading = false;
      },
      error: () => {
        this.loading = false;
      },
    });
  }
}
